// JavaScript Document
$(document).ready(function() {
    'use strict';

    var windHeight = $(window).height();
    var winWidth = $(window).width();
    if (winWidth >= 1000) {
        $('#homeSlider .item').css({
            'height': windHeight + 'px'
        });

        $('#sequence').css({
            'height': windHeight + 'px'
        });
    }

    $('#whBanner').carousel({
        interval: 10000
    })
    $('#team-carousel').carousel({
        interval: 10000
    })
    $('#cfeed').carousel({
        interval: 10000
    })
    $('#blogCarousel').carousel({
        interval: 10000
    })
    $(".navbar-collapse li").click(function() {
        $('.navbar-collapse').removeClass('in');

    });

    // indicator opset
    var offsetGetst = $('.getst').offset().top;
    $('#homeSlider .carousel-indicators').css('top', offsetGetst + 'px');

    // chart
    $('.chart').circliful();

    // // Background Video
    // var options = { videoId: 'impZ7krcPzI', start: 3 };
    // $('#bgvideo').tubular(options);
});


$(window).load(function() {
    'use strict';

    /* Page Scroll to id fn call */
    $(".navbar-nav a,a[href='#top'],a[rel='m_PageScroll2id']").mPageScroll2id({
        layout: "auto",
        offset: 10,
        highlightSelector: ".navbar-nav a"
    });

    /* demo functions */
    $("a[rel='next']").click(function(e) {
        e.preventDefault();
        var to = $(this).parent().parent("section").next().attr("id");
        $.mPageScroll2id("scrollTo", to);
    });

    /* Istop */

    if ($(".portfolioContainer").length > 0) {
        var $container = $('.portfolioContainer');
        $container.isotope({
            animationOptions: {
                duration: 800,
                easing: 'linear',
                queue: false
            },
            masonry: {
                columnWidth: 0
            }
        });

        $(window).resize(function() {
            var $container = $('.portfolioContainer');
            $container.isotope({
                animationOptions: {
                    duration: 800,
                    easing: 'linear',
                    queue: false
                },
                masonry: {
                    columnWidth: 0
                }
            });
        });


        // filter items when filter link is clicked
        $('#portfolioFilter a').click(function() {
            var selector = $(this).attr('data-filter');
            $container.isotope({
                filter: selector
            });

            $('#portfolioFilter .current').removeClass('current');
            $(this).addClass('current');
            return false;
        });
    }

    /*Parallax Background
    ------------------------------------ */
    $('.parallax_a').parallax('50%', .2);
    $('.parallax_b').parallax('50%', .2);
    $('.parallax_c').parallax('50%', .2);
    $('.parallax_d').parallax('50%', .2);
    $('.parallax_e').parallax('50%', .2);
});

function sliderControlSize() {
    var get_window_height = $(window).innerHeight();
    var get_window_width = $(window).width();
    $('#sequence').css({ "height": get_window_height });

}
jQuery(document).load(function() {
    sliderControlSize();

});
jQuery(window).resize(function() {
    sliderControlSize();

})
jQuery(document).ready(function() {
    sliderControlSize();
});